(42, 'site.id_title_why', 'ID Title Why on Us', 'Kenapa Berinvestasi di Jawa Tengah?', NULL, 'text', 37, 'Site'),
(43, 'site.id_ket_why', 'ID Keterangan Why on Us', 'Jawa Tengah didukung oleh iklim investasi yang kondusif, mulai dari pertumbuhan ekonomi yang baik, peningkatan infrastruktur, upah yang kompetitif, dan kemudahan perizinan', NULL, 'text_area', 38, 'Site'),
(44, 'site.id_title_prospective', 'ID Prospective Title', 'Berikut adalah beberapa Proyek Prospektif', NULL, 'text', 39, 'Site'),
(45, 'site.id_ket_prospective', 'ID Keterangan Prospective', 'Peluang investasi yang prospektif untuk ditawarkan kepada investor, memenuhi Rencana Pembangunan Nasional/Regional Jangka Menengah, lokasi yang ditawarkan sesuai dengan peruntukan dalam Rencana Tata Ruang, dan telah memiliki studi pendahuluan.', NULL, 'text_area', 40, 'Site'),
(46, 'site.id_title_potential', 'ID Potential Title', 'Berikut adalah beberapa Proyek Potensial', NULL, 'text', 41, 'Site'),
(47, 'site.id_ket_potential', 'ID Keterangan Potential', 'Potensi investasi dalam bentuk sumber daya yang tersedia di suatu daerah yang memiliki nilai ekonomi tetapi masih belum dieksplorasi.', NULL, 'text_area', 42, 'Site'),
(48, 'site.id_title_rto', 'ID Ready to Offer Title', 'Berikut adalah beberapa Proyek Potensial', NULL, 'text', 43, 'Site'),
(49, 'site.id_ket_rto', 'ID Keterangan Ready to Offer', 'Peluang investasi yang siap ditawarkan kepada investor, sesuai RPJMN/ RPJMD, lokasi yang ditawarkan sesuai dengan peruntukan dalam Rencana Tata Ruang, status kepemilikan yang jelas dan bersih (tidak dalam sengketa), dan layak dari aspek hukum, teknis dan keuangan berdasarkan Pra Studi Kelayakan.', NULL, 'text_area', 44, 'Site'),
(50, 'site.title_faq', 'FAQ Title', NULL, NULL, 'text', 45, 'Site'),
(51, 'site.ket_faq', 'Keterangan FAQ', NULL, NULL, 'text_area', 46, 'Site'),
